import React, {useState} from 'react';

function GuestForm() {
    const INITIAL_DATA = {artist:"", city:"", distance:""};
    const [formData, setFormData] = useState(INITIAL_DATA);

    return (
        <form>
            <label htmlFor="artist">Which artist would you like to see?</label>
            <input type="text" value={formData.artist} />
            <label htmlFor="city">Enter your city:</label>
            <input type="text" value={formData.city} />
            <label htmlFor="distance">How far are you willing to travel to see this artist?</label>
            <input type="number" value={formData.distance} />

            <button>Find my artist!</button>
        </form>

    )
}

export default GuestForm();
