import './App.css';
import Navbar from './Navbar';
import AppRoutes from './Routes';
import ArtistTrackerApi from './api';
import {useNavigate} from 'react-router-dom';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [currUser, setCurrUser] = useState(localStorage.getItem('currUser'));
  try {
    if (currUser) {
      JSON.parse(localStorage.getItem('currUser'));
    }
  } catch (err) {
    console.log('currUser is undefined', err);
  }

  const navigate = useNavigate();

  async function signup(newUser) {
    const authToken = await ArtistTrackerApi.registerUser(newUser).catch((err) => {
      console.log(err);
    });
    setToken(authToken);
    const user = await ArtistTrackerApi.getUser(newUser.username);
    setCurrUser(user);
    navigate('/');
  }

  return (
      <div className="App">
          <Navbar />
          <div className="App-main container-fluid">
            <AppRoutes />
          </div>
      </div>
  );
}

export default App;
