import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function GuestForm() {
    const INITIAL_DATA = {artist:"", city:"", distance:""};
    const [formData, setFormData] = useState(INITIAL_DATA);

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    const handleClick = evt => {
        evt.preventDefault();
        console.log('GUEST FORM BUTTON CLICKED.')
    }

    return (
        // <form>
        //     <label htmlFor="artist">Which artist would you like to see?</label>
        //     <input type="text"
        //            name="artist" 
        //            value={formData.artist}
        //            onChange={handleChange} />
        //     <label htmlFor="city">Enter your city:</label>
        //     <input type="text"
        //            name="city" 
        //            value={formData.city}
        //            onChange={handleChange} />
        //     <label htmlFor="distance">How far are you willing to travel to see this artist?</label>
        //     <input type="number"
        //            name="distance" 
        //            value={formData.distance}
        //            onChange={handleChange} />

        //     <button onClick={handleClick}>Find my artist!</button>
        // </form>
        <Form>
            <Form.Group className="mb-3" controlId="guestFormArtist">
                <Form.Label>Which artist would you like to see?</Form.Label>
                <Form.Control type="text" 
                        name="artist" 
                        value={formData.artist}
                        onChange={handleChange}/>
            </Form.Group>
            <Form.Group className="mb-3" controlId="guestFormCity">
                <Form.Label>Enter your city:</Form.Label>
                <Form.Control type="text"
                        name="city" 
                        value={formData.city}
                        onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="guestFormDistance">
                <Form.Label>How far are you willing to travel to see this artist?</Form.Label>
                <Form.Control type="number"
                        name="distance" 
                        value={formData.distance}
                        onChange={handleChange} />
            </Form.Group>
            <Button variant="primary" type="submit" onClick={handleClick}>
                Submit
            </Button>
        </Form>

    )
}



export default GuestForm;
