import React, { useState, useEffect, useContext } from 'react';
import ArtistTrackerApi from './api';
import Button from 'react-bootstrap/Button';
import "./EventList.css";
import userContext from './userContext';

/** Renders a list of events in table format for an artist.
 * 
 *  If user is logged in, shows button to save an event to their account.
 */

function EventList( {artistId, cityInfo, radius} ) {
    const [events, setEvents] = useState([]);
    const [artist, setArtist] = useState({});
    const {currUser} = useContext(userContext);

    // Get artist details when page loads
    useEffect(() => {
        async function setArtistData() {
           await getArtistDetails();
        }
        setArtistData();
    }, [])

    useEffect(() => {
        if (artist && cityInfo.latitude) getEvents();
    }, [cityInfo])

    async function getArtistDetails() {
        const artistRes = await ArtistTrackerApi.getArtistById(artistId);
        if (artistRes) setArtist(artistRes.artist);
    }

    async function getEvents() {
        try {
            const eventsRes = await ArtistTrackerApi.getEventsForArtist(
                artistId, cityInfo.latitude, cityInfo.longitude, radius)
            if (eventsRes) setEvents(eventsRes.events);
        } catch(err) {
            console.log(err)
        }
    }

    async function saveEvent(e) {
        try {
            const res = await ArtistTrackerApi.addEventToUser(e, currUser.id);
        } catch(err) {
            console.log(err);
        }
        //use e.id call a save route
    }

    return(
        <div className="EventList container">
            <div className="card mb-3">
                <div className="card-body">
                    <img className="img-fluid" src={artist.image} alt="artist" />
                    <h5 className="card-title text-dark h1">{artist.name}</h5>
                    {(events.length)
                    ? <table className='table'>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Venue</th>
                                <th>City</th>
                                <th>State</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                        {events.map(e => {
                            return (<tr key={e.id}>
                                        <td>{new Date(e.datetime).toLocaleDateString()}</td>
                                        <td>{e.venue}</td>
                                        <td>{e.venueCity}</td>
                                        <td>{e.venueState}</td>
                                        {currUser &&
                                            <td><Button size="sm" onClick={() => saveEvent(e)}>Save</Button></td>
                                        }
                                    </tr>)
                        })
                        }     
                        </tbody>
                    </table>
                    :
                    <div className='text-dark'>NO EVENTS WITHIN SEARCH RADIUS</div>
                    }
                </div>
                {/* end card body */}
            </div>
            {/* end card */}
        </div> 
    )
}

export default EventList;
