import React, {useContext} from 'react';
import Button from 'react-bootstrap/Button';
import AuthContext from './authContext';
import ArtistTrackerApi from './api';

function HomeUser() {
    const {currUser} = useContext(AuthContext);

    async function getUsername(id) {
        const res = await ArtistTrackerApi.getUser(id);
        console.log(res)
        return res.username;
    }

    const user = getUsername(+currUser)


    return (
        <div className="container-fluid">
            <div className="row justify-contents-center py-2">
                <h1>Hi there, {user}!</h1>
            </div>
            <div className="row mb-3 py-4 border border-success">
                <div className="col-9 ">
                    <h3>Check out these shows from your favorite artists:</h3>
                    LIST OF SHOWS GOES HERE.
                </div>
                <div className="col-3 p-4 justify-content-center border border-primary">
                    <Button variant="primary" className="mb-3">Add an artist</Button> 
                    <Button variant="primary">Change my settings</Button>
                </div>
            </div>
        </div>
    )
}

export default HomeUser;
