import React, { useState, useContext} from 'react';
import { useParams } from 'react-router-dom';
import AuthContext from './authContext';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import UnauthorizedMessage from './UnauthorizedMessage';


function Profile() {  
    const {id} = useParams();
    const {currUser, token} = useContext(AuthContext);
    const [formData, setFormData] = useState(
        {username: currUser.username, firstName: currUser.firstName, 
         email: currUser.email, city: currUser.city, 
         distancePref: currUser.distancePref}
    );

    if (!token) return <UnauthorizedMessage />;

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    return (
        <div className="container-fluid">
            <div className="row justify-contents-center py-2">
                <p className='display-5'>Profile for: {currUser.username}</p>
            </div>
            <div className="row mb-3 py-4 border border-success">
                <Form className="col-10 border border-danger">
                <div className="col-6 p-4 justify-content-center border border-primary">
                    <Form.Group className="mb-3" controlId="username">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text"
                                    name="username" 
                                    value={formData.username}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password"
                                    name="password" 
                                    value={formData.password}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="firstName">
                        <Form.Label>First Name</Form.Label>
                        <Form.Control type="text"
                                    name="firstName" 
                                    value={formData.firstName}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email"
                                    name="email" 
                                    value={formData.email}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="city">
                        <Form.Label>City</Form.Label>
                        <Form.Control type="text"
                                    name="city" 
                                    value={formData.city}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="distancePref">
                        <Form.Label>Event search radius</Form.Label>
                        <Form.Control type="number"
                                    name="distancePref" 
                                    value={formData.distancePref}
                                    onChange={handleChange} />
                    </Form.Group>
                </div>
                <div className="col-4 p-4 justify-content-center border border-primary">
                        Artist list here.
                    <Button type="submit">Save changes</Button>
                </div>
                </Form>
            </div>
        </div>
    )
}

export default Profile;
