import React, { useState, useEffect } from 'react';
import ArtistTrackerApi from './api';

function EventList( {data} ) {
    const [artists, setArtists] = useState([]);

    useEffect(() => {
        async function addArtistToState(name) {
            const res = await ArtistTrackerApi.getArtistByName(name);
            console.log('RES-artist to add to state:', res)
            setArtists([...artists, {
                id: res.id, 
                name: res.artistName, 
                url: res.url,
                image: res.image}])
        }
        data.forEach(elem => {
            addArtistToState(elem.artistName);
            console.log('ARTISTS-in async', artists)
        });
    }, [])


    return(
        <div>
            <ul>
                {artists.map(a => {
                   return <li key={a.artistName}>{a.artistName}</li>
                })}
            </ul>
        </div>
    )
}

export default EventList;
