import React, {useEffect, useState, useCallback} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './GuestForm.css';
import EventList from './EventList';
import ArtistTrackerApi from './api';
const _ = require('lodash');
const {debounce} = _;

function GuestForm() {
    const INITIAL_DATA = {artistName:"", city:"", distance:"", citySearch:""};
    const [formData, setFormData] = useState([INITIAL_DATA]);
    const [submitted, setSubmitted] = useState(false);
    const [cities, setCities] = useState([]);
    const debounceLoadData = useCallback(
                                debounce(str => fetchCities(str), 1000), []);

    async function fetchCities(str) {
        try {
            const res = await ArtistTrackerApi.getCitiesForAutocomplete(str);
            setCities(res.cities);
        } catch(err) {
            console.log(err);
        }  
    }

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });

        if(formData.citySearch && formData.citySearch.length >= 4){
            debounceLoadData(formData.citySearch);
        }
    }

    const updateCity = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [formData.city]: value}
        });
    }

    const handleSubmit = evt => {
        evt.preventDefault();
        const searchData = {artistName: formData.artistName,
                        city: formData.city,
                        distance: formData.distance}
        setFormData(searchData);
        setSubmitted(true);
    }

    return (
        (submitted === false)
        ?
        <>
            <Form className="GuestForm" onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formArtist">
                    <Form.Label>Which artist would you like to see?</Form.Label>
                    <Form.Control type="text"
                                  name="artistName" 
                                  value={formData.artistName ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formCitySearch">
                    <Form.Label>Enter your city:</Form.Label>
                    <Form.Control type="text"
                                  name="citySearch" 
                                  value={formData.citySearch ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Select className="mb-3" size='5' onChange={updateCity}>
                    {cities.map (el => {
                        return <option key={el.id} value={el}>{el.name}, {el.region}</option>
                    })}
                </Form.Select>
                <Form.Group className="mb-3" controlId="formDistance">
                    <Form.Label>How far are you willing to travel to see this artist?</Form.Label>
                    <Form.Control type="number"
                                  name="distance" 
                                  value={formData.distance ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>

                <Button type="submit">Find my artist!</Button>
            </Form>
        </>
        :
        <>
            <EventList data={formData} />
        </>
    )
}

export default GuestForm;
