import React, {useEffect, useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './GuestForm.css';
import EventList from './EventList';
const axios = require ('axios');

function GuestForm() {
    const INITIAL_DATA = {artistName:"", city:"", distance:""};
    const [formData, setFormData] = useState([INITIAL_DATA]);
    const [submitted, setSubmitted] = useState(false);
    const [cities, setCities] = useState([]);

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    const handleSubmit = evt => {
        evt.preventDefault();
        const searchData = {artistName: formData.artistName,
                        city: formData.city,
                        distance: formData.distance}
        setFormData(searchData);
        setSubmitted(true);
    }

    useEffect(() => {
        async function getCities(str) {
            const res = await axios.get(
                `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?namePrefix=${formData.city}&apiKey=853b9f4f9cmsh8a4230f74741b00p1a6d06jsn97aeefa48514&countryIds=US`
            )
            console.log('city info:', res.data)
            return res.data;
        }
        if (formData.city && formData.city.length >= 3) {
            setCities(getCities(formData.city))
            console.log('cities state:', cities)
        }
    }, [formData.city])

    const selectArr = ['A', 'B', 'C'];

    return (
        (submitted === false)
        ?
        <>
            <Form className="GuestForm" onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formArtist">
                    <Form.Label>Which artist would you like to see?</Form.Label>
                    <Form.Control type="text"
                                  name="artistName" 
                                  value={formData.artistName ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formCity">
                    <Form.Label>Enter your city:</Form.Label>
                    <Form.Control type="text"
                                  name="city" 
                                  value={formData.city ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formDistance">
                    <Form.Label>How far are you willing to travel to see this artist?</Form.Label>
                    <Form.Control type="number"
                                  name="distance" 
                                  value={formData.distance ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Select aria-label="Default select example">
                    {selectArr.map (el => {
                        return <option value={el}>{el}</option>
                    })}
                </Form.Select>
                <Button type="submit">Find my artist!</Button>
            </Form>
        </>
        :
        <>
            <EventList data={formData} />
        </>
    )
}

export default GuestForm;
