import React, { useState, useContext} from 'react';
import { useParams } from 'react-router-dom';
import AuthContext from './authContext';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import UnauthorizedMessage from './UnauthorizedMessage';
import './Profile.css';


function Profile() {  
    const {id} = useParams();
    const {currUser, token} = useContext(AuthContext);
    const [formData, setFormData] = useState(
        {username: currUser.username, firstName: currUser.firstName, 
         email: currUser.email, city: currUser.city, 
         distancePref: currUser.distancePref}
    );

    if (!token) return <UnauthorizedMessage />;

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    return (
        <div className="container-fluid">
            <div className="row justify-content-center py-2">
                <p className='display-5'>Profile for: {currUser.username}</p>
            </div>
            <div >
                <Form className="Profile row mb-3 py-4 justify-content-center">
                <div className="col-5 p-4 justify-content-center border border-primary">
                    <Form.Group as={Row} className="mb-3" controlId="username">
                        <Form.Label column sm={3}>Username</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="text"
                                        name="username" 
                                        value={formData.username}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-3" controlId="password">
                        <Form.Label column sm={3}>Password</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="password"
                                        name="password" 
                                        value={formData.password}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-3" controlId="firstName">
                        <Form.Label column sm={3}>First Name</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="text"
                                        name="firstName" 
                                        value={formData.firstName}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-3" controlId="email">
                        <Form.Label column sm={3}>Email</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="email"
                                        name="email" 
                                        value={formData.email}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-3" controlId="city">
                        <Form.Label column sm={3}>City</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="text"
                                        name="city" 
                                        value={formData.city}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-3" controlId="distancePref">
                        <Form.Label column sm={3}>Event search radius</Form.Label>
                        <Col sm={5}>
                            <Form.Control type="number"
                                        name="distancePref" 
                                        value={formData.distancePref}
                                        onChange={handleChange} />
                        </Col>
                    </Form.Group>
                </div>
                <div className="col-5 ms-5 p-4 justify-content-center border border-primary">
                        Artist list here.
                    <Button type="submit">Save changes</Button>
                </div>
                </Form>
            </div>
        </div>
    )
}

export default Profile;
