import React, { useState, useEffect, useContext } from 'react';
import ArtistTrackerApi from './api';
import Button from 'react-bootstrap/Button';
import "./EventList.css";
import userContext from './userContext';

/** Renders a list of events in table format for an artist.
 * 
 *  If user is logged in, shows button to save an event to their account.
 */

function EventList( {artistId, cityInfo, radius} ) {
    const [events, setEvents] = useState([]);
    const [artist, setArtist] = useState({});
    const {currUser} = useContext(userContext);

    // Get artist details when page loads
    useEffect(() => {
        async function setArtistData() {
           await getArtistDetails();
        }
        setArtistData();
    }, [])

    useEffect(() => {
        getEvents()
    }, [])

    async function getArtistDetails() {
        const artistRes = await ArtistTrackerApi.getArtistById(artistId);
        if (artistRes) setArtist(artistRes.artist);
    }

    // async function getCityDetails() {
    //     const cityRes = await ArtistTrackerApi.getCityById(cityId);
    //     if (cityRes) setCity(cityRes.city);
    // }

    async function getEvents() {
        try {
            const eventsRes = await ArtistTrackerApi.getEventsForArtist(
                artistId, cityInfo.latitude, cityInfo.longitude, radius)
            if (eventsRes) setEvents(eventsRes.events);
        } catch(err) {
            console.log('getEvents error:', err)
        }
    }

    return(
        <div className="EventList container p-0 border border-light">
            <div className="row align-items-center">
                <div className="col-2">
                    <img src={artist.image} alt='artist' className='img-fluid'/>
                </div>
                <div className='EventList-title col-10'>
                    <div className='row'>
                        <p className="fs-2">{artist.name}</p>
                    </div>
                </div>
                <div className='row'>
                    {(events.length)
                    ?
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Venue</th>
                                <th>City</th>
                                <th>State</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {events.map(e => {
                                    return (<tr key={e.id}>
                                        <td>{new Date(e.datetime).toLocaleDateString()}</td>
                                        <td>{e.venue}</td>
                                        <td>{e.venueCity}</td>
                                        <td>{e.venueState}</td>
                                        {currUser &&
                                        <td><Button size="sm">Save</Button></td>
                                        }
                                        </tr>)
                                })
                            }     
                        </tbody>
                    </table>
                    :
                    <div className='p-5'>NO EVENTS AT THIS TIME</div>
                }
                </div>    
            </div>
        </div>
    )
}

export default EventList;
