import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function GuestForm() {
    const INITIAL_DATA = {artist:"", city:"", distance:""};
    const [formData, setFormData] = useState(INITIAL_DATA);

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    const handleSubmit = evt => {
        evt.preventDefault();
        console.log('GUEST FORM BUTTON CLICKED.')
        setFormData(INITIAL_DATA);
    }

    return (
        <>
            <Form onSubmit={handleSubmit}>
                <label htmlFor="artist" className="form-label">
                    Which artist would you like to see?
                </label>
                <input className="form-control"
                    type="text"
                    name="artist" 
                    value={formData.artist}
                    onChange={handleChange} />
                <label htmlFor="city" className="form-label mt-2">
                    Enter your city:
                </label>
                <input className="form-control"
                    type="text"
                    name="city" 
                    value={formData.city}
                    onChange={handleChange} />
                <label htmlFor="distance" className="form-label mt-2">
                    How far are you willing to travel to see this artist?</label>
                <input className="form-control"
                    type="number"
                    name="distance" 
                    value={formData.distance}
                    onChange={handleChange} />
                <button className="btn btn-primary mt-2" type="submit">
                    Submit
                </button>
            </Form>
        </>
    )
}

export default GuestForm;

