import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './SignupForm.css';

function SignupForm() {
    const INITIAL_DATA = {
        username: "",
        password: "",
        firstName: "",
        email: "",
        city: "",
        distancePref: ""
    }
    const [formData, setFormData] = useState(INITIAL_DATA);

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    const handleSubmit = evt => {
        evt.preventDefault();
        console.log('SIGNUP FORM BUTTON CLICKED.')
        setFormData(INITIAL_DATA);
    }

    return (
        <div>
            <div className="container-fluid">
                <div className="row justify-contents-center py-2">
                    <h1>Create an Account</h1>
                </div>
            <div className="row mb-3 py-4 border border-success">
                <div className="col-9 ">
                    <Form className="SignupForm" onSubmit={handleSubmit}>
                        <Form.Group as={Row} className="mb-3" controlId="formUsername">
                            <Form.Label column sm={2}>Username</Form.Label>
                            <Col sm={4}>
                                <Form.Control type="text"
                                            name="username" 
                                            value={formData.username}
                                            onChange={handleChange}  />
                             </Col>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password"
                                          name="password" 
                                          value={formData.password}
                                          onChange={handleChange}  />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formFirstName">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control type="text"
                                          name="firstName" 
                                          value={formData.firstName}
                                          onChange={handleChange}  />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email"
                                          name="email" 
                                          value={formData.email}
                                          onChange={handleChange}  />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formCity">
                            <Form.Label>City</Form.Label>
                            <Form.Control type="text"
                                          name="city" 
                                          value={formData.city}
                                          onChange={handleChange}  />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formDistance">
                            <Form.Label>Preferred search distance (miles)</Form.Label>
                            <Form.Control type="number"
                                          name="distancePref" 
                                          value={formData.distancePref}
                                          onChange={handleChange}  />
                        </Form.Group>
                        <Button type="submit" variant="primary">Submit</Button>
                    </Form>
                </div>
            </div>
        </div>
        </div>
    )
}

export default SignupForm;
