import React, { useState, useEffect, useContext } from 'react';
import ArtistTrackerApi from './api';
import Button from 'react-bootstrap/Button';
import "./EventList.css";
import userContext from './userContext';

/** Renders a list of events in table format for an artist.
 * 
 *  If user is logged in, shows button to save an event to their account.
 */

function EventList( {artistId, cityId, radius} ) {
    const [events, setEvents] = useState([]);
    const [city, setCity] = useState({});
    const [artist, setArtist] = useState({});
    const {currUser} = useContext(userContext);

    async function getArtistDetails() {
        const artistRes = await ArtistTrackerApi.getArtistById(artistId);
        setArtist(artistRes.artist);
    }

    async function getCityDetails() {
        console.log('IN getCityDetails for id:', cityId)
        const cityRes = await ArtistTrackerApi.getCityById(cityId)
        console.log('cityRes',cityRes)
        setCity(cityRes.city);
        console.log('CITY AFTER SETTING:', city)
    }

    async function getEvents() {
        const eventsRes = await ArtistTrackerApi.getEventsForArtist(
            artistId, city.latitude, city.longitude, radius)
        if (eventsRes.events) setEvents(eventsRes.events);
    }

    // Get city and artist details when page loads
    useEffect(() => {
        getCityDetails();
        getArtistDetails();
    }, [])

    getEvents();




    return(
        <div className="EventList container p-0 border border-light">
            <div className="row align-items-center">
                <div className="col-2">
                    <img src={artist.image} alt='artist' className='img-fluid'/>
                </div>
                <div className='EventList-title col-10'>
                    <div className='row'>
                        <p className="fs-2">{artist.name}</p>
                    </div>
                </div>
                <div className='row'>
                    {(events.length)
                    ?
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Venue</th>
                                <th>City</th>
                                <th>State</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {events.map(e => {
                                    return (<tr key={e.id}>
                                        <td>{new Date(e.datetime).toLocaleDateString()}</td>
                                        <td>{e.venue}</td>
                                        <td>{e.venueCity}</td>
                                        <td>{e.venueState}</td>
                                        {currUser &&
                                        <td><Button size="sm">Save</Button></td>
                                        }
                                        </tr>)
                                })
                            }     
                        </tbody>
                    </table>
                    :
                    <div className='p-5'>NO EVENTS AT THIS TIME</div>
                }
                </div>    
            </div>
        </div>
    )
}

export default EventList;
