import Button from 'react-bootstrap/Button';
import AuthContext from './authContext';
import ArtistTrackerApi from './api';

function HomeUser() {
    const {currUser} = useContext(AuthContext);

    async function getUsername(id) {
        try {
            const res = await ArtistTrackerApi.getUser(id);
            console.log('RES', res)
            return res.username;
        } catch(err) {
            console.log("WELL THAT DIDN'T WORK")
        }

    }

    const userID = +(JSON.parse(currUser));
    console.log('userID', typeof userID, userID)
    const user = getUsername(JSON.parse(currUser))

// ******** Trying to get the username from the id (string) in localStorage
