                <div className="col-9 ">
                    <h3>Check out these shows from your favorite artists:</h3>
                    LIST OF SHOWS GOES HERE.
                </div>
                <div className="col-3 p-4 justify-content-center border border-primary">
                    <Button variant="primary" className="mb-3">Add an artist</Button> 
                    <Button variant="primary">Change my settings</Button>
                </div>
            </div>
        </div>
    )
}

export default HomeUser;
