import React, {useEffect, useState} from 'react';
import Figure from 'react-bootstrap/Figure';
import ArtistTrackerApi from './api';
import './ArtistItem.css';

function ArtistItem( {artistId} ) {
    const [artist, setArtist] = useState({});

    useEffect(() => {
        async function findArtist(id) {
            try {
                const res = await ArtistTrackerApi.getArtistById(id);
                setArtist(res.artist);
            } catch(err) {
                console.log(err);
            }
        }
        findArtist(artistId); 
    }, [])


    return (
        <div>
            <figure className="ArtistItem-figure">
                <img src={artist.image} className="figure-img img-fluid rounded" alt="artist" />
                <figcaption className="figure-caption text-right">{artist.name}</figcaption>
            </figure>


        </div>
    )

}

export default ArtistItem;
