import React, {useEffect, useState} from 'react';
import Figure from 'react-bootstrap/Figure';
import ArtistTrackerApi from './api';
import './ArtistItem.css';

function ArtistItem( {artistId} ) {
    const [artist, setArtist] = useState({});

    useEffect(() => {
        async function findArtist(id) {
            try {
                const res = await ArtistTrackerApi.getArtistById(id);
                setArtist(res.artist);
            } catch(err) {
                console.log(err);
            }
        }
        findArtist(artistId); 
    }, [])

    const remove = (evt) => {
        evt.preventDefault();
        console.log('clicked to remove artist')
    }
    return (
        <div className='border-bottom p-1'>
            {artist.name}
            <button className="ArtistItem-button align-bottom" onClick={remove}>X</button>            
        </div>
    )

}

export default ArtistItem;
