import React, {useEffect, useState, useContext} from 'react';
import ArtistTrackerApi from './api';
import userContext from './userContext';
import './ArtistItem.css';

function ArtistItem( {artistId, updateUserArtistsInState} ) {
    const [artist, setArtist] = useState({});
    const {currUser} = useContext(userContext);

    useEffect(() => {
        async function findArtist(id) {
            try {
                const res = await ArtistTrackerApi.getArtistById(id);
                setArtist(res.artist);
            } catch(err) {
                console.log(err);
            }
        }
        findArtist(artistId); 
    }, [])

    const remove = async (evt) => {
        evt.preventDefault();
        await ArtistTrackerApi.removeArtistFromUser(currUser.id, artistId)
        updateUserArtistsInState();
    }
    return (
        <div className='border-bottom p-1'>
            {artist.name}
            <button className="ArtistItem-button mt-1" onClick={remove}>X</button>         
        </div>
    )

}

export default ArtistItem;
