import React, {useContext} from 'react';
import Button from 'react-bootstrap/Button';
import userContext from './userContext';
import { useNavigate } from 'react-router-dom';
import EventList from './EventList';
import './Home.css';

function HomeUser( {logout} ) {
    const {currUser, usersSavedArtists} = useContext(userContext);
    const navigate = useNavigate();

    function addArtist(evt) {
        evt.preventDefault();
        navigate('/addArtist');
    }

    const city = async function getCityDetails() {
        const cityRes = await ArtistTrackerApi.getCityById(currUser.city);
        if (cityRes) setCity(cityRes.city);
    }

    function handleLogout(evt) {
        evt.preventDefault();
        logout();
    }

    return (
        <div className="container-fluid">
            <div className="row py-2">
                <p className="display-5">Hi there, {currUser.firstName}!</p>
            </div>
            <div className="row mb-3 py-4 border border-success">
                <div className="col-9 ">
                    <p className="h3">Check out these shows from your favorite artists:</p>
                    {(usersSavedArtists.length)
                        ? usersSavedArtists.map(a => {
                            return <EventList key={a.id} artistId={a.id} cityInfo={city} radius={currUser.distance_pref}/>;
                        })
                        : <p>NO SAVED ARTISTS</p>
                    }
                </div>
                <div className="col-3 p-4 align-items-center">
                    <div className="row mb-3">
                        <Button variant="primary" 
                                className="col-6 ms-auto"
                                onClick={addArtist}>Add an artist</Button>
                    </div>
                    <div className="row mb-3">
                        <Button variant="primary" href={`/profile/${currUser.id}`}
                                className="col-6 ms-auto">
                            Change my settings
                        </Button>
                    </div>
                    <div className="row mb-3">
                        <Button variant="outline-primary" onClick={handleLogout} className="col-6 ms-auto">
                            Log out
                        </Button>
                    </div>    
                </div>
            </div>
        </div>
    )
}

export default HomeUser;
