import './App.css';
import { useState, useEffect } from 'react';
import Navbar from './Navbar';
import AppRoutes from './Routes';
import ArtistTrackerApi from './api';
import { useNavigate } from 'react-router-dom';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [currUser, setCurrUser] = useState(localStorage.getItem('currUser'));

  try {
    if (currUser) {
      JSON.parse(localStorage.getItem('currUser'));
    }
  } catch (err) {
    console.log('currUser is undefined', err);
  }

  const navigate = useNavigate();

  useEffect(function saveCredentialsToLocalStorage() {
    localStorage.setItem('token', token);
    localStorage.setItem('currUser', JSON.stringify(currUser));
  }, [token, currUser])

  async function login(username, password) {
    try {
      const authToken = await ArtistTrackerApi.authenticateUser(username, password);
      const user = await ArtistTrackerApi.getUser(username);
      if (authToken && user) {
        setToken(authToken);
        setCurrUser(user);
        navigate('/'); 
      } else throw new Error()
    } catch(err) {
      alert ('Invalid username/password.')
    }
  }

  async function signup(newUser) {
    const authToken = await ArtistTrackerApi.registerUser(newUser).catch((err) => {
      console.log(err);
    });
    setToken(authToken);
    const user = await ArtistTrackerApi.getUser(newUser.username);
    setCurrUser(user);
    navigate('/');
  }

  return (
      <div className="App">
          <Navbar />
          <div className="App-main container-fluid">
            <AppRoutes />
          </div>
      </div>
  );
}

export default App;
