import React, {useEffect, useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './GuestForm.css';
import EventList from './EventList';
import ArtistTrackerApi from './api';
const axios = require ('axios');

function GuestForm() {
    const INITIAL_DATA = {artistName:"", city:"", distance:""};
    const [formData, setFormData] = useState([INITIAL_DATA]);
    const [submitted, setSubmitted] = useState(false);
    const [cities, setCities] = useState([]);

    // fetches cities for autocomplete
    // async function getCities(str) {
    //     try {
    //         console.log('running GetCities with', str)
    //         const citiesAPI = '853b9f4f9cmsh8a4230f74741b00p1a6d06jsn97aeefa48514';
    //         const url = 'https://wft-geo-db.p.rapidapi.com/v1/geo/cities';
    //         const headers = { 'x-rapidapi-key':citiesAPI };


    //         const res = await axios.get(
    //             `${url}?namePrefix=${str}&sort=-population&minPopulation=20000&apiKey=${citiesAPI}&countryIds=US`,
    //             {headers}
    //         )
    //         setCities(res.data.data);
    //     } catch(err) {
    //         console.log(err);
    //     }
    // }

    async function fetchCities(str) {
        try {
            console.log('fetching ', str)
          const res = await ArtistTrackerApi.getCitiesForAutocomplete(str); 
            console.log('Got the cities:', res)  
        } catch(err) {
            console.log(err);
        }  
    }

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
        if (formData.city && formData.city.length >= 4) {
            console.log('gonna fetch those cities...', formData.city)
             fetchCities(formData.city);
        }
    }

    const handleSubmit = evt => {
        evt.preventDefault();
        const searchData = {artistName: formData.artistName,
                        city: formData.city,
                        distance: formData.distance}
        setFormData(searchData);
        setSubmitted(true);
    }

    // useEffect(() => {
    //     // fetches cities for autocomplete
    //     async function getCities(str) {
    //         try {
    //             const citiesAPI = '853b9f4f9cmsh8a4230f74741b00p1a6d06jsn97aeefa48514';
    //             const url = 'https://wft-geo-db.p.rapidapi.com/v1/geo/cities';
    //             const headers = { 'x-rapidapi-key':citiesAPI };

    //             const res = await axios.get(
    //                 `${url}?namePrefix=${str}&sort=-population&minPopulation=20000&apiKey=${citiesAPI}&countryIds=US`,
    //                 {headers}
    //             )
    //             setCities(res.data.data);
    //         } catch(err) {
    //             console.log(err);
    //         }
    //     }

    //     if (formData.city && formData.city.length >= 4) {
    //        getCities(formData.city);  
    //     }
    // }, [formData.city])

    const selectArr = ['A', 'B', 'C'];

    return (
        (submitted === false)
        ?
        <>
            <Form className="GuestForm" onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formArtist">
                    <Form.Label>Which artist would you like to see?</Form.Label>
                    <Form.Control type="text"
                                  name="artistName" 
                                  value={formData.artistName ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formCity">
                    <Form.Label>Enter your city:</Form.Label>
                    <Form.Control type="text"
                                  name="city" 
                                  value={formData.city ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formDistance">
                    <Form.Label>How far are you willing to travel to see this artist?</Form.Label>
                    <Form.Control type="number"
                                  name="distance" 
                                  value={formData.distance ?? ""} 
                                  onChange={handleChange} />
                </Form.Group>
                <Form.Select aria-label="Default select example">
                    {cities.map (el => {
                        return <option key={el.id} value={el}>{el.name}, {el.region}</option>
                    })}

                </Form.Select>
                <Button type="submit">Find my artist!</Button>
            </Form>
        </>
        :
        <>
            <EventList data={formData} />
        </>
    )
}

export default GuestForm;
