import React, { useState, useContext, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ArtistTrackerApi from './api';
import AuthContext from './authContext';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import UnauthorizedMessage from './UnauthorizedMessage';


function Profile() {  
    const {id} = useParams();
    const {token} = useContext(AuthContext);
    const [user, setUser] = useState({id: "", username: "",
                    firstName: "", email: "", city: "", distancePref: 0});
    const [formData, setFormData] = useState(
        {username: user.username, firstName: user.firstName, email: user.email,
         city: user.city, distancePref: user.distancePref}
    );

    useEffect( () => {
        async function getUserDetails(userId) {
            try {
                const user = await ArtistTrackerApi.getUser(userId, token);
                setUser(user);
            } catch(err) {
                console.log(err)
            }
        }
        getUserDetails(+id)
    }, [id])

    if (!token) return <UnauthorizedMessage />;

    const handleChange = evt => {
        const {name, value} = evt.target;
        setFormData(data => { 
            return {...data, [name]: value}
        });
    }

    return (
        <div className="container-fluid">
            <div className="row justify-contents-center py-2">
                <h1>Profile for: {user.username}</h1>
            </div>
            <div className="row mb-3 py-4 border border-success">
                <Form>
                    <Form.Group className="mb-3" controlId="username">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text"
                                    name="username" 
                                    value={formData.username}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formCity">
                        <Form.Label>Enter your city:</Form.Label>
                        <Form.Control type="text"
                                    name="city" 
                                    value={formData.city}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formDistance">
                        <Form.Label>How far are you willing to travel to see this artist?</Form.Label>
                        <Form.Control type="number"
                                    name="distance" 
                                    value={formData.distance}
                                    onChange={handleChange} />
                    </Form.Group>
                    <Button type="submit">Find my artist!</Button>
                </Form>
            </div>
        </div>
    )
}

export default Profile;
