import React, {useContext, useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import ArtistTrackerApi from './api';
import AuthContext from './authContext';

function HomeUser() {
    const {currUser, token} = useContext(AuthContext);
    const [user, setUser] = useState();

    useEffect( () => {
        async function getUserDetails(userId) {
            try {
                const user = await ArtistTrackerApi.getUser(userId, token);
                setUser(user);
            } catch(err) {
                console.log(err)
            }
        }
        getUserDetails(+currUser.id)
    }, [])

    return (
        <div className="container-fluid">
            <div className="row justify-contents-center py-2">
                <h1>Hi there, {user.firstName}!</h1>
            </div>
            <div className="row mb-3 py-4 border border-success">
                <div className="col-9 ">
                    <h3>Check out these shows from your favorite artists:</h3>
                    LIST OF SHOWS GOES HERE.
                </div>
                <div className="col-3 p-4 justify-content-center border border-primary">
                    <Button variant="primary" className="mb-3">Add an artist</Button> 
                    <Button variant="primary" href={`/profile/${currUser.id}`}>Change my settings</Button>
                </div>
            </div>
        </div>
    )
}

export default HomeUser;
