import React, {useEffect, useState, useContext} from 'react';
import ArtistTrackerApi from './api';
import userContext from './userContext';
import './ArtistItem.css';

function ArtistItem( {artist, setUsersSavedArtists} ) {
    const {currUser} = useContext(userContext);

    const remove = async (evt) => {
        evt.preventDefault();
        const message = await ArtistTrackerApi.removeArtistFromUser(currUser.id, artist.id);
        setUsersSavedArtists(usersSavedArtists => 
            usersSavedArtists.filter(a => a.id !== artist.id));

        alert(message.deleteMessage);
    }

    return (
        <div className='border-bottom p-1'>
            {artist.name}
            <button className="ArtistItem-button mt-1" onClick={remove}>X</button>         
        </div>
    )

}

export default ArtistItem;
