    //const {currUser} = useContext(userContext);

    const handleClick = (evt) => {
        evt.preventDefault();
        remove(artist.id)
    }

    return (
        <div className='border-bottom p-1'>
            {artist.name}
            <button className="ArtistItem-button mt-1" onClick={handleClick}>X</button>         
        </div>
    )

}

